import React from "react";
import classNames from "../utils/class-names";

export default function startAndStop({ isTimerRunning, disabled }){
    function playPause() {
        setDisabled(false)
        setIsTimerRunning((prevState) => {
          const nextState = !prevState;
          if (nextState) {
            setSession((prevStateSession) => {
              // If the timer is starting and the previous session is null,
              // start a focusing session.
              if (prevStateSession === null) {
                return {
                  label: "Focusing",
                  timeRemaining: focusDuration * 60,
                };
              }
              return prevStateSession;
            });
          }
          return nextState;
        });
      }
    
      function stop() {
        // sets session to default values besides timer settings
        setIsTimerRunning((state) => state = false)
        setSession((state) => state = null)
        setDisabled((state) => state = true)
      }
    return (
        <div className="row">
            <div className="col">
                <div
                    className="btn-group btn-group-lg mb-2"
                    role="group"
                    aria-label="Timer controls"
                >
                    <button
                    type="button"
                    className="btn btn-primary"
                    data-testid="play-pause"
                    title="Start or pause timer"
                    onClick={playPause}
                    >
                    <span
                        className={classNames({
                        oi: true,
                        "oi-media-play": !isTimerRunning,
                        "oi-media-pause": isTimerRunning,
                        })}
                    />
                    
                    </button>
                    <button
                    type="button"
                    className="btn btn-secondary"
                    data-testid="stop"
                    disabled={disabled}
                    onClick={stop}
                    title="Stop the session"
                    >
                    <span className="oi oi-media-stop" />
                    </button>
                </div>
            </div>
        </div>
    )
}
